s items\" do","    todo_list.todo_items.create(content: \"Milk\")","    todo_list.todo_items.create(content: \"Eggs\")","    ","    visit_todo_list(todo_list)","    ","    expect(page.all(\"ul.todo_items li\").size).to eq(2)","    ","    within \"ul.todo_items\" do","      expect(page).to have_content(\"Milk\")","      expect(page).to have_content(\"Eggs\")","    end","  end","end"]}]}]]},"ace":{"folds":[],"scrolltop":0,"scrollleft":0,"selection":{"start":{"row":37,"column":3},"end":{"row":37,"column":3},"isBackwards":false},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"firstLineState":0},"timestamp":1423693818973,"hash":"d3f5106745e23d7826177f7a77116f3feb77c389"}